options.handler:

function() {
	element=document.getElementById(this.id);

     var data = [];
    var table = element.closest( 'table' );
    var input = table.getElementsByTagName( 'input' );
    for ( var z = 0; z < input.length; z++ ) {
        data.push( input[z].id );
    }

  Ext.Ajax.request({
    url: 'CryptoGatePaymentCheck/test?apiToken='+ document.getElementById(data[0]).value + '&apiUrl=' + encodeURI(document.getElementById(data[1]).value),
    success: function (response) {
      console.log(response);
      Shopware.Msg.createGrowlMessage(response.statusText, response.responseText)
    },
    failure: function (response) {
      if (response.status === 404) {
        Shopware.Msg.createGrowlMessage('Plugin Manager', 'Please activate plugin before testing api.');
      } else {
        Shopware.Msg.createGrowlMessage(response.statusText, response.responseText)
      }
    }
  });
}
